
import React from "react";
import Slider from "react-slick";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { useNavigate } from "react-router-dom";

const NextArrow = ({ onClick }) => {
  return (
    <button
      onClick={onClick}
      className="absolute top-1/2 right-0 z-10 -translate-y-1/2 transform rounded-full bg-blue-600 p-2 text-white shadow hover:bg-blue-700"
    >
      <FaChevronRight size={20} />
    </button>
  );
};

const PrevArrow = ({ onClick }) => {
  return (
    <button
      onClick={onClick}
      className="absolute top-1/2 left-0 z-10 -translate-y-1/2 transform rounded-full bg-blue-600 p-2 text-white shadow hover:bg-blue-700"
    >
      <FaChevronLeft size={20} />
    </button>
  );
};

const BrandSlider = () => {
  const navigate = useNavigate();

  const brands = [
    {
      name: "Cipla",
      logo: "data:image/png;base64,",
    },
    {
      name: "Dr.Reddy's",
      logo: "data:image/jpeg;base64,",
    },
    {
      name: "Glenmark",
      logo: "data:image/png;base64,",
    },
    {
      name: "Sun Pharma",
      logo: "https://images.seeklogo.com/logo-png/31/2/sun-pharma-logo-png_seeklogo-314019.png"
    },
    {
      name: "Intas",
      logo: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQDxvsD849nI9RneCx3DL-xeg53D6G1CgS99A&s",
    },
    {
      name: "Aurobindo Pharma",
      logo: "data:image/png;base64,",
    },
    {
      name: "Torrent Pharma",
      logo: "data:image/jpeg;base64,",
    },
    {
      name: "Biocon",
      logo: "data:image/png;base64,",
    },
  ];

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 2,
    autoplay: true,
    autoplaySpeed: 3000,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 3, slidesToScroll: 2 } },
      { breakpoint: 768, settings: { slidesToShow: 2, slidesToScroll: 1 } },
      { breakpoint: 480, settings: { slidesToShow: 1, slidesToScroll: 1 } },
    ],
  };

  return (
    <Slider {...settings}>
      {brands.map((brand, index) => (
        <div
          key={index}
          className="cursor-pointer p-4"
          onClick={() =>
            navigate(`/products?brand=${encodeURIComponent(brand.name)}`)
          }
        >
          <div className="rounded-lg border bg-white p-4 shadow hover:shadow-lg flex flex-col items-center">
            <img
              src={brand.logo}
              alt={brand.name}
              className="mb-2 h-24 w-24 rounded-full object-cover border"
            />
            <p className="text-center font-medium">{brand.name}</p>
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default BrandSlider;


